@section('style')
    <style>
        .popup-table {
            border-collapse: collapse;
            width: 100%;
        }

        .popup-table th,
        .popup-table td {
            border: 1px solid #ddd;
            padding: 8px;
        }

        .popup-table th {
            padding-top: 12px;
            padding-bottom: 12px;
            text-align: left;
            background-color: #f2f2f2;
        }

        .popup {
            position: fixed;
            top: 50%;
            right: 10px;
            transform: translateY(-50%);
            background-color: #fff;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
            z-index: 1000;
            padding: 20px;
            border-radius: 8px;
            max-width: 90%;
            max-height: 90%;
            overflow-y: auto;
        }

        .close-icon {
            cursor: pointer;
            position: absolute;
            top: 10px;
            right: 10px;
        }

        .container {
            width: 90%;
            margin: 15px auto;
        }

        h2 {
            text-align: center;
        }

        .animation {
            position: relative;
        }

        .animation:hover {
            transition: transform 0.3s ease-in-out;
            transform-origin: center;
            transform: scale(1.04);
            z-index: 1;
            fill: #226742;
        }

        @media (max-width: 768px) {
            .popup {
                width: 90%;
            }

            .container {
                width: 100%;
            }
        }
    </style>
